    public function __construct()
    {
        add_shortcode('icon', array($this, 'icon_func'));
    }

    public function icon_func($atts)
    {
        $a = shortcode_atts([
            'type' => ''
        ], $atts);

        $iconType = strtolower($a['type']);

        if (!isset($iconType) || !isset($this->icons[$iconType])) {
            return false;
        }

        return $this->icons[$iconType];
    }
